function Svg({ bg, children }: { bg: string; children: React.ReactNode }) {
  return (
    <svg viewBox="0 0 40 40" xmlns="http://www.w3.org/2000/svg">
      <rect width="40" height="40" fill={bg} />
      {children}
    </svg>
  );
}

